#include <bits/stdc++.h>
using namespace std;
const int64_t base=998244353;
int64_t power(int64_t x, int64_t y){
  if(y==0) return 1;
  int64_t t=power(x, y/2);
  t=(t*t)%base;
  if(y%2) t=(t*x)%base;
  return t;
}
int64_t inverse(int64_t x){//modular inverse
  return power(x, base-2);
}
vector <int64_t> f({1}), iv({1});

int64_t C(int64_t n, int64_t k){
    return ((f[n]*iv[k]%base)*iv[n-k])%base;
}
int64_t star(n, k){
    return C(n+k-1, k-1);
}
int64_t countGame(int64_t n, int64_t k, int64_t x){//n points total, k players, no player can have x point or more
  if(k==0) return !(n==0);
  int64_t ans=0
  for(int i=0; i<=k; i++){
    int64_t t=n-x*i;
    if(t<0) break;
    if(i%2) ans=(ans-C(k, i)*star(t, k))%base;
    else ans=(ans+C(k, i)*star(t, k))%base;
  }
  return ans
}
int main(){
  int p, s, r;
  int64_t gameSize=countGame(s-r, p, 1e18);
  gameSize=inverse(gameSize);
  cin>>p>>s>>r;
  for(int i=1; i<5555; i++){
    f.push_back((f[i-1]*i)%base);
    iv.push_back(inverse(f[i]));
  }
  int64_t ans=0;
  for (int q=r; q<=s; q++){
    for(int i=0; i<p; i++){//exactly i people have the same score
        int64_t t=s-(i+1)*q;
        if(t<0) break;
        ans=(ans+(((C(p-1, i)*count_game(t, p-i-1, q))%base)*gameSize%base)*inverse(i+1))%base;
    }
  }
  cout<<ans<<'\n';
}

