int cnt(int a,int b, int c){
	int sum = 0;
	if(a == 1 && b == 1 && c == 1){
		sum = 1;
		return sum;
	}else if(a == 1 && b == 1 && c != 1){
		sum = c;
		return sum;
	}else if(a == 1 && c == 1 && b != 1){
		sum = b;
		return sum;
	}else if(c == 1 && b == 1 && a != 1){
		sum = a;
		return sum;
	}else{
		sum = 2*(a + b + c) - 6;
	}
	if(a == 1 && b != 1 && c != 1){
		return sum + cnt(a,b-2,c-2); 
	}else if(b == 1 && a != 1 && c != 1){
		return sum + cnt(a-2,b,c-2);
	}else if(c == 1 && b != 1 && a != 1){
		return sum + cnt(a-2,b-2,c);
	}
	return sum + cnt(a-1,b-1,c-1);
}

int main(){
	int a,b,c;
	cin>>a>>b>>c;
	int sum = 0;
	// algorithm
