long long int square(long long int x,int n)
{
    if(n==0)
        return 1;
    else if(n==1)
        return x;
    else if(n%2==0)
        return square((x*x)%MOD,(n/2));
    else
        return (x*square((x*x)%MOD,((n-1)/2)))%MOD;
}

