int ans = 0;

void solve(int lim) {
	queue<int> bfs;
	if (4 <= lim)
		bfs.push(4);
	if (7 <= lim)
		bfs.push(7);
	while (!bfs.empty()) {
		int u = bfs.front();
		bfs.pop();
		++ans;
		int v = u * 10 + 4;
		if (v <= lim)
			bfs.push(v);
		v = u * 10 + 7;
		if (v <= lim)
			bfs.push(v);
	}
}

bool lucky(int num) {
	while (num > 0) {
		int lol = num % 10;
		if (lol != 4 && lol != 7)
			return false;
		num /= 10;
	}
	return true;
}

