          temp[j][p]=(temp[j][p]+dp[j][p]*step(2,p))%mod;
      }

      for(int j=0;j<=i;j++)
        for(int p=0;p<=i;p++)
            dp[j][p]=temp[j][p];
      memset(temp,0,sizeof(temp));
   }

}
