#include <bits/stdc++.h>
using namespace std;

#define MAX 501
#define MOD 1000000007
#define ll long long

ll dp[MAX][MAX][MAX];

ll mhp , mat, mde;
ll h , a , b;
ll get(ll hp , ll at  , ll de){
    ll dx1 = max(0ll ,at-mde); // m lost
    ll dx2 = max(0ll ,mat-de); // y lost

    if(!dx1) return hp>0 && mhp <=0 ;

    ll dy = mhp / dx1  + (mhp % dx1!=0) ;
    hp -= dy * dx2;

    return hp > 0;
}

ll solve(ll hp , ll at , ll de){
    if(hp>=300  || at>=300 || de>=300 )
        return 1e9;

    if(get(hp,at,de))
        return 0;

    if(dp[hp][at][de]+1)
        return dp[hp][at][de];

    ll res1 = h+solve(hp+1,at,de);
    ll res2 = a+solve(hp,at+1,de);
    ll res3 = b+solve(hp,at,de+1);

    return dp[hp][at][de] = min(min(res1,res2),res3);  
}


int main(){

#ifndef ONLINE_JUDGE
