    freopen("output.txt", "w", stdout);
#endif // !ONLINE_JUDGE

    ll hp,at,de;
    cin>>hp>>at>>de;
    cin>>mhp>>mat>>mde;
    cin>>h>>a>>b;

    memset(dp,-1,sizeof dp);

    cout<<solve(hp,at,de);


}
