ll x,n;
bool prime[1000000000]={false};
vector<int> G;
ll cond=1;

ll quick_pow(ll x,ll n,ll m){
	ll res = 1;
	while(n > 0){
		if(n & 1)	res = res * x % m;
		x = x * x % m;
		n >>= 1;
	}
	return res;
}

void prime_num(ll x){// 
	for(ll i=2;i<=sqrt(x);i++){
		if(prime[i]==false){
			for(ll j=2;j*i<=x;j++){
				prime[i*j]=true;
			}
		}
	} 
}


void g(ll x,ll y){
	ll t=x;
	ll tot=0;
	while(t<=y){
		tot+=y/t;
		if(t>y/x)break;
		t=t*x;
	}
	cond=(cond*quick_pow(x,tot,INF))%INF;
} 

int main(){
	scanf("%lld%lld",&x,&n);
	prime_num(x);
	for(ll i=2;i<=min(x,n);i++){
		if(prime[i]==false&&x%i==0){
			G.push_back(i);
		}
	}
	for(ll j=0;j<G.size();j++){
		g(G[j],n);
	}
	printf("%lld",cond);
	return 0;	
}

