inline LL QuickPow(LL x,LL y,LL Mod)
{
	LL Res = 1, temp = x;
	while ( y )
	{
		if (y & 1)
			Res = Res * temp % Mod;
		temp = temp * temp % Mod;
		y /= 2;
	}
	return Res;
}
inline LL Inv(LL x)
{
	return QuickPow(x, p - 2, p); 
}
inline LL Mo(LL x,LL Mod)
{
	return ((x % Mod) + Mod) % Mod;
}
inline Jie(LL n,LL Mi)
{
	LL Get = b * Inv( Mi ) % p;
	LL Dai = (p - 1) * Mo(n - Get, p) + n;
	if (Dai > x)
		return 0;
	return (x - Dai) / (p * (p - 1)) + 1;
}
