using namespace std;

typedef long long ll;

// returns (d, x, y) such that d = gcd(a, b) = ax + by
ll extended_euclid(ll a, ll b, ll &x, ll &y)
{
    if (b == 0) { x = 1, y = 0; return a; }
    ll r = extended_euclid(b, a % b, y, x);
    y -= a / b * x;
    return r;
}

ll chinese_remainder_theorem(ll a, ll m)
{
    ll s = m, t, ans = 0, p, q;
    t = s / m;
    extended_euclid(t, m, p, q);
    ans = (ans + t * p * a) % s;
    if (ans < 0) ans += s;
    return ans;
}

ll pow(ll a, ll b, ll M)
{
	ll x = 1;
	for (; b > 0; b >>= 1)
	{
		if (b & 1) x = x * a % M;
		a = a * a % M;
	}
	return x;
}

typedef long double ld;
#define y1 Y1
#define double ld
ll minE_max(double x)
{
	ll e = x;
	if (e - 1 >= x) return e - 1;
	if (e >= x) return e;
	return e + 1;
}

ll maxE_min(double x)
{
	ll e = x;
	if (e + 1 <= x) return e + 1;
	if (e <= x) return e;
	return e - 1;
}

ll x1, x2, y1, y2;
// returns (x, y) such that c = ax + by
ll diophantine_equation(ll a, ll b, ll c)
{
	ll g, x, y;
	ll g = extended_euclid(a, b, x, y);
	if (c % g != 0) return 0;

	if(g * b < 0) swap(x1, x2);
	if(g * a > 0) swap(y1, y2);

	return max((min(maxE_min(double(x2 * g - x * c) / b), maxE_min(double(y2 * g - y * c) / -a))
			- max(minE_max(double(x1 * g - x * c) / b), minE_max(double(y1 * g - y * c) / -a)) + 1), 0LL);
}

int main()
{
    ios_base::sync_with_stdio(0), cin.tie(0);

    ll a, b, p, l;
    cin >> a >> b >> p >> l;

    if (a%p == 0)
    {
    	cout << (b%p==0) * l;
    	return 0;
    }

    vector<ll> r;
    set<ll> s;
    ll x = a % p;
    do
    {
        r.push_back(x);
        s.insert(x);
        x = (x * a) % p;
    } while ((ll)r.size() < l && s.find(x) == s.end());

    ll k = r.size();
    ll ans = 0;
    for (int i = 1; i <= k; ++i)
    {
        a = r[i-1];
        ll x = chinese_remainder_theorem((b * pow(a, p-2, p)) % p, p);
        if (k == p)
        {
        	ll z = (i - x) / p;
        	if (z * p == (i - x))
        	{
        		ll mn_t1 = max((1-x+p-1)/p, 0LL);
        		ll mx_t1 = (l-x)/p;
        		if (mx_t1 - mn_t1 >= 0)
        		{
            		ll mn_t2 = max((1-i+p-1)/p, 0LL);
            		ll mx_t2 = (l-i)/p;
            		if (mx_t2 - mn_t2 >= 0)
            		{
            			mn_t2 = max(mn_t2, mn_t1 - z);
            			mx_t2 = min(mx_t2, mx_t1 - z);
            			if (mx_t2 - mn_t2 >= 0)
            				ans += mx_t2 - mn_t2 + 1;
            		}
        		}
        	}
        }
        else
        {
        	x1 = x==0;
        	x2 = (l-x)/p;
        	y1 = 0;
        	y2 = (l-i)/k;
        	if (x1 <= x2 && y1 <= y2)
        		ans += diophantine_equation(p, -k, i-x);
        }
    }

    cout << ans;

    return 0;
}

