unsigned long long mature;

void getMature()
{
	mature = 0;
	char c;
	for (int i = 0; i < n; i++)
	{
		cin >> c;
		if (c == '1')
			mature++;
	}
}

unsigned long long getCost(unsigned long long size, unsigned long long count)
{
	return count * (c1 + c2 * (size - 1) * (size - 1));
}

unsigned long long check(unsigned long long group)
{
	unsigned long long k = n / group;
	unsigned long long r = n % group;
	unsigned long long smallGroup = group - r;
	unsigned long long bigGroup = r;
	return getCost(k, smallGroup) + getCost(k + 1, bigGroup);
}

unsigned long long all()
{
	unsigned long long min = pow(2, 64) - 1;
	unsigned long long current;
	for (int i = mature; i > 0; i--)
	{
		current = check(i);
		if (current < min)
			min = current;
	}
	return min;
}

int main()
{
	cin >> n >> c1 >> c2;
	getMature();
	cout << all();
	return 0;
}

