const int mod=1e9+7,N=100005;
typedef long long ll;
int x,n,pos;
int cnt1,cnt2;
int fac[N],inv[N];
int qpow(int a,int b)
{
    int res=1,tp=a;
    while(b)
    {
        if(b&1)
            res=1ll*res*tp%mod;
        tp=1ll*tp*tp%mod;
        b>>=1;
    }
    return res;
}
ll P(int n,int m)
{
    return 1ll*fac[n]*inv[n-m]%mod;
}
int main()
{
    scanf("%d%d%d",&n,&x,&pos);
    fac[0]=1,inv[0]=1;
    for(int i=1;i<=N;i++) fac[i]=fac[i-1]*1ll*i%mod;
    for(int i=1;i<=N;i++) inv[i]=qpow(fac[i],mod-2);
    int l=0,r=n;
    while(l<r)
    {
        int mid=(l+r)/2;
        if(mid<=pos)
            l=mid+1,cnt1+=(mid<pos);
        else
            r=mid,cnt2++;
    }
    ll ans=1ll*P(x-1,cnt1)*
           P(n-x,cnt2)%mod*
           fac[n-cnt1-cnt2-1]%mod;
    printf("%lld\n",ans);
    return 0;
}

