    for(LL i=2;i<=1000000;i++) fac[i]=fac[i-1]*i%p,inv[i]=(p-p/i)*inv[p%i]%p;
    for(LL i=2;i<=1000000;i++) inv[i]=inv[i-1]*inv[i]%p;
}
LL C(LL m,LL n) {return fac[m]*inv[m-n]%p*inv[n]%p;}
