		//printf("%lld:\n",i);
		for(LL j=0;j<=min(k-1,i-1);j++)
		{
			(f[i]+=f[i-j-1]*C(i-1,j)%p*fac[j]%p)%=p;
			//printf("%lld:%lld\n",j,f[i]);
		}
		//printf("\n");
	}
	LL ans=0;
	//for(int i=0;i<=n;i++) printf("%lld ",f[i]);printf("\n");
	for(LL i=1;i<=n;i++)
	{
		(ans+=f[i-1]*C(n-1,i-1)%p*fac[n-i]%p)%=p;
