ll DP[20][(1<<8)][2520];


//    pos    = ()
//    pre    = ()
//    status = ,1,,
//          +1
//    limit  = ,567,6,
//          4,0-95,
//          ,5
//          limit=1,0-6
//
//    DP
ll   dfs(int pos,int pre,int status,int limit)
{
    //,""
    if(pos < 1)
    {
    	for(int i = 0;i < 8;i ++)
    	{
    		if((pre&(1<<i))&&(status%(i + 2)!=0))
    			return 0;
    	}
    	return 1;
    }

    //DP,,,
    if(!limit && DP[pos][pre][status] != -1)
        return    DP[pos][pre][status];

    int    end = limit ? bit[pos] : 9;
    ll    ret = 0;

    //,status||
    //limti&&
    //567,46X,6,
    //,,,56
    //
    //"49"
    for(int i = 0;i <= end;i ++)
    {
    	if(i>=2)
        ret += dfs(pos - 1,(pre|(1<<(i-2))),(status*10+i)%2520,limit && (i == end));
        else
        ret += dfs(pos - 1,pre,(status*10+i)%2520,limit && (i == end));
    }


    //DP
    if(!limit)
        DP[pos][pre][status] = ret;

    return ret;
}
ll solve(ll n) {
	int len = 0;
	while (n) {
		bit[++len] = n % 10;
		n /= 10;
	}
	bit[len + 1] = 0;
	return dfs(len,0,0,1);
}

