char *decimal_to_binary(long long x)
{
    char *s = new char[100];
    if (x == 0)
    {
        s[0] = '0';
        s[1] = NULL;
        return s;
    }
    long long nr = 0, i, j;
    while (x != 0)
    {
        if (x % 2 == 0)
        {
            s[nr] = '0';
        }
        else
        {
            s[nr] = '1';
        }
        nr++;
        x = x / 2;
    }
    s[nr] = NULL;
    char *ans = new char[100];
    ans[nr] = NULL;
    for (i = 0; i < nr; i++)
    {
        ans[nr - i - 1] = s[i];
    }
    return ans;
}
