ll add(ll x, ll y)
{
	x += y;
	while (x >= MOD) x -= MOD;
	while (x < 0) x += MOD;
	return x;
}

ll sub(ll x, ll y)
{
	return add(x, -y);
}

ll mul(ll x, ll y)
{
	return (x * y) % MOD;
}

ll binpow(ll x, ll y)
{
	ll z = 1;
	while (y > 0)
	{
		if (y % 2 == 1) z = mul(z, x);
		x = mul(x, x);
		y /= 2;
	}
	return z;
}

ll inv(ll x)
{
	return binpow(x, MOD - 2);
}

ll divide(ll x, ll y)
{
	return mul(x, inv(y));
}

vector<ll> factorial(1,1);

ll fact(ll n){
    while (factorial.size() < n+5){
    factorial.PB(mul(factorial.size(), factorial.back()));
    }

    return factorial[n];
}

ll nChoosek( ll n, ll k ){
    if ((k>n) || (k<0)) return 0;
    ll sz = factorial.size();
    while (sz < n + 1){
        factorial.PB(mul(factorial.size(), factorial.back()));
        sz++;
    }
    return divide(factorial[n], mul(factorial[k], factorial[n-k]));
}

