using namespace std;

const int MAXN = 35000000;
const int MOD = 1e9 + 7;
bool prime[MAXN + 10];
vector <ll> primes;
vector <pair <ll, int> > fact;
vector <ll> divi;
pii dp[70000][10002];

ll gcd(ll a, ll b) {
  if (b == 0) return a;
  return gcd(b, a % b);
}

void sito() {
  FOR(i, 2, MAXN) {
    if (prime[i]) continue;

    primes.push_back((ll) i);
    for (int j = i + i; j < MAXN; j += i) prime[j] = true;
  }
  return;
}

void rek(int x, ll a) {
  if (x == fact.size()) {
    divi.push_back(a);
    return;
  }

  ll tr = 1;
  REP(i, fact[x].second + 1) {
    rek(x + 1, a * tr);
    tr *= fact[x].first;
  }
  return;
}

pii add(pii a, pii b) {
  ll A = (ll) a.first * b.second + (ll) b.first * a.second;
  ll B = (ll) a.second * b.second;
  //ll C = gcd(A, B);

  return {A % MOD, B % MOD};
}

pii mul(pii a, pii b) {
  ll A = (ll) a.first * b.first;
  ll B = (ll) a.second * b.second;
  //ll C = gcd(A, B);

  return {A % MOD, B % MOD};
}

ll pot(ll x, ll a) {
  ll out = 1, pot = x;
  while (a) {
    if (a & 1) out = (out * pot) % MOD;
    pot = (pot * pot) % MOD;
    a >>= 1;
  }
  return out;
}

int main() {
  ios_base::sync_with_stdio(false);
  sito();

  REP(i, 100000) {
    REP(j, 55) {
      dp[i][j] = {-1, -1};
    }
  }

  ll n, nn;
  int k;
  cin >> n >> k;
  nn = n;

  for (int x : primes) {
    int t = 0;
    while (n % x == 0) {
      n /= x;
      t++;
    }
    if (t > 0) fact.push_back({x, t});
  }
  if (n > 1) fact.push_back({n, 1});

  rek(0, 1);
  sort(divi.begin(), divi.end());

  REP(x, divi.size()) {
    REP(y, k + 1) {
      if (x == 0) {
        dp[x][y] = {1, 1};
        continue;
      }
      if (y == 0) {
        dp[x][y] = {divi[x], 1};
        continue;
      }

      pii tr = {0, 1};
      int tt = 0;
      REP(i, x + 1) {
        if (divi[x] % divi[i] == 0) {
          tr = add(tr, dp[i][y - 1]);
          tt++;
        }
      }
      tr = mul(tr, {1, tt});

      dp[x][y] = tr;
    }
  }

  pii out = dp[divi.size() - 1][k];
  ll sol = (out.first * pot(out.second, MOD - 2)) % MOD;
  cout << sol << "\n";

  return 0;
}

