const double eps = 1e-10;
const int oo = ~0u >> 2, mo = (int) 1e9 + 7;
const int mn = (int) 1e6 * 2 + 2;

int fac[mn], rev[mn];
int k, w, Ans(0);

int pow(int e, int x){
	int ret = 1;
	while (x){
		if (x & 1) ret = Mul(ret, e);
		e = Mul(e, e), x >>= 1;
	}
	return ret;
}

int Get_Rev(int x){
	return pow(x, mo - 2);
}

int calc1(int t, int d){
	int mol = Mul(Mul(pow(k, w - t - d - 1), t), Mul(fac[k], fac[k]));
	int den = Mul(rev[k - t], rev[k - t - d]);
	int ret = Mul(mol, den);
	return ret;
}

int calc2(int t, int d){
	int mol = Mul(Mul(fac[k], w - d), fac[k - t - d + w - 1]);
	int den = Mul(rev[k - t], rev[k - t - d]);
	int ret = Mul(mol, den);
	return ret;
}

int main(){
	fac[0] = rev[0] = 1;
	fo (i, 1, mn - 1) fac[i] = Mul(fac[i - 1], i), rev[i] = Mul(rev[i - 1], Get_Rev(i));
	cin >> k >> w;
	fo (i, 1, k){
		int t;
		if (i <= w - 1) t = calc1(i, 0), Ans = Plus(Ans, t);
		if (i > w - 1) t = calc2(i, 0), Ans = Plus(Ans, t);
		if (i < k && i + 1 <= w - 1) t = calc1(i, 1), Ans = Plus(Ans, t);
		if (i < k && i + 1 > w - 1) t = calc2(i, 1), Ans = Plus(Ans, t);
	}
	cout << Ans << endl;
}

