void dfs1(int x)
{
	int tp,y,cnt = 0,m1 = 0,m2 = 0;

	for(tp = head[x]; y = spot[tp],tp; tp = nex[tp])
		if(a[y]>=lim && y!=fa[x]) {
			dfs1(y);
			if(!c[y]) cnt += f[y];
			else {
				if(f[y]>m2) m2 = f[y];
				if(m1<m2) swap(m1,m2);
			}
		}
	maxcolor[x][0] = m1;
	maxcolor[x][1] = m2;
	f[x] = cnt+m1+1;
}

void dfs2(int x, int &ret)
{
	if(fa[x]) {//x 
		if(c[fa[x]]-(c[x]!=0)>0) {
			//xcolor 
			if(c[x]) {//x 
				if(f[x]<maxcolor[fa[x]][0])//x 
					g[x] = f[x]-maxcolor[x][0]+max(maxcolor[x][0], g[fa[x]]);
				else // 
					g[x] = f[x]-maxcolor[x][0]+max(maxcolor[x][0],
						g[fa[x]]-maxcolor[fa[x]][0]+maxcolor[fa[x]][1]);
						// 
			}
			else //x 
				g[x] = f[x]-maxcolor[x][0]+max(maxcolor[x][0], g[fa[x]]-f[x]);
		}
		else {
			//x
			if(c[x]) {
				if(f[x]<maxcolor[fa[x]][0])
					g[x] = f[x]+g[fa[x]];
				else
					g[x] = f[x]+g[fa[x]]-maxcolor[fa[x]][0]+maxcolor[fa[x]][1];
			}
			else
				g[x] = g[fa[x]];//f[x]+g[fa[x]]-f[x] 
		}
		c[x] += ((c[fa[x]]-(c[x]!=0))!=0);
	}
	else g[x] = f[x];
//	printf("g[%d] = %d\t", x, g[x]);
	ret = max(ret, g[x]);
	for(int tp = head[x]; tp; tp = nex[tp])
		if(a[spot[tp]]>=lim && spot[tp]!=fa[x])
			dfs2(spot[tp], ret);
}
int solve()
{
	memset(v, 0, sizeof(v));
	memset(fa, 0, sizeof(fa));
	memset(maxcolor, 0, sizeof(maxcolor));
	memset(c, 0, sizeof(c));
	int maxn = 0;
//	cout <<"solve " <<lim<<endl;
	for(int i = 1; i<=n; i++)
		if(a[i]>=lim && v[i]==0) {
//			cout << "dfs from "<<i<<endl;
			dfs0(i), dfs1(i), dfs2(i,maxn);
//			cout << endl;
		}
//	cout << maxn << endl;
//	for(int i = 1; i<=n; i++) cout << color[i]<<' ';
///	cout << endl;
	return maxn>=m;
}

