#include<bits/stdc++.h>
using namespace std;
int n,k,arr[1<<18];
bool vis[1<<18],big[1<<18];int failed[1<<18],failed1[1<<18];
vector<int>adj[1<<18];
pair<int,int>mx[1<<18],mx1[1<<18];
int sum[1<<18];
void dfs(int x,int p)
{
    int sz=adj[x].size(),nxt;
    bool ok=1;
    pair<int,int>pii;
    sum[x]=1;
    vis[x]=1;
    for(int i=0;i<sz;++i)
    {
        nxt=adj[x][i];
        if(nxt==p)continue;
        if(vis[nxt]){failed1[x]=failed[x];failed[x]=nxt;continue;}
        dfs(nxt,x);
        if(failed[nxt]){
            pii=make_pair(sum[nxt]+mx[nxt].first,nxt);
            if(pii>mx[x])swap(pii,mx[x]);
            if(pii>mx1[x])swap(pii,mx1[x]);
        }
        else sum[x]+=sum[nxt];
        if(failed[nxt]){failed1[x]=failed[x];failed[x]=nxt;continue;}
    }
}
void dfs1(int x,int p)
{
    //cout<<x<<" "<<sum[x]<<" "<<mx[x].first<<" "<<mx1[x].first<<" h\n";
    if(p){
        pair<int,int>pii=mx[p];
        if(mx[p].second==x)pii=mx1[p];
        if(!failed[p]){
            sum[x]+=sum[p]-sum[x];
        }
        else if(failed[p]==x&&!failed1[p]){
            sum[x]+=sum[p];
        }
        else pii.first+=sum[p];
        //cout<<x<<" "<<p<<" "<<pii.first<<endl;
        if(pii>mx[x])swap(pii,mx[x]);
        if(pii>mx1[x])swap(pii,mx1[x]);
        //change failed
        if((failed[p]&&failed[p]!=x)||(failed1[p]&&failed1[p]!=x)){
            failed1[x]=failed[x];
            failed[x]=p;
        }
    }
       // cout<<x<<" "<<sum[x]<<" "<<mx[x].first<<" "<<mx1[x].first<<" f\n";

    int sz=adj[x].size(),nxt;
    for(int i=0;i<sz;++i){
        nxt=adj[x][i];
        if(nxt==p)continue;
        if(!big[nxt])continue;
        dfs1(nxt,x);
    }
}
bool check(int mid)
{
    //cout<<mid<<endl;
    for(int i=1;i<=n;++i){
        big[i]=(arr[i]>=mid);
        vis[i]=(arr[i]<mid);
        mx[i]=mx1[i]=make_pair(0,0);
        sum[i]=0;
        failed[i]=failed1[i]=0;
    }
    for(int i=1;i<=n;++i){
        if(!vis[i]){
            dfs(i,0);
            dfs1(i,0);
        }
    }
    //for(int i=1;i<=n;++i)cout<<i<<" "<<sum[i]<<" "<<mx[i].first<<" "<<mx1[i].first<<" "<<failed[i]<<endl;
    //cout<<"---------------------------\n";
    for(int i=1;i<=n;++i){
        if(big[i]){
            if(sum[i]+mx[i].first>=k)return 1;
        }
    }
    return 0;

}
int main()
{
    cin>>n>>k;
    for(int i=1;i<=n;++i)scanf("%d",arr+i);
    int u,v;
    for(int i=1;i<n;++i){
        scanf("%d%d",&u,&v);
        adj[u].push_back(v);
        adj[v].push_back(u);
    }
    int st=1,en=1000002,mid;
    //check(700000);return 0;
    while(st<en){
        mid=(st+en)/2;
        if(check(mid))st=mid+1;
        else en=mid;
    }
    cout<<en-1;

}

#include<bits/stdc++.h>
using namespace std;
int n,k,a[200005];
vector<int>adj[200005];
bool comp[200005];
int sum[200005];
pair<int,int>mx[2][200005];
pair<int,int>dfs(int x,int p,int z)
{

    comp[x]=1;
    pair<int,int> m;
    sum[x]=1;
    for(int i=0;i<adj[x].size();++i){
        if(adj[x][i]==p)continue;
        m=dfs(adj[x][i],x,z);
        if(m.second)sum[x]+=m.first;
        else {
            comp[x]=0;
            m.second=adj[x][i];
            if(m>mx[0][x])swap(m,mx[0][x]);
            if(m>mx[1][x])swap(m,mx[1][x]);
        }
    }
    if(a[x]<z){
        comp[x]=0;
        sum[x]=0;
        mx[0][x]=mx[1][x]=make_pair(-1,0);
        return make_pair(0,0);
    }
    if(comp[x])return make_pair(sum[x],1);
    return make_pair(sum[x]+mx[0][x].first,0);
}
void dfs1(int x,int p){
    if(p){
        if(!comp[p]&&(mx[1][p].first!=-1||mx[0][p].second!=x)){
            pair<int,int>m;
            m=mx[0][p];
            if(m.second==x)m=mx[1][p];
            m.first+=sum[p];
            if(comp[x])m.first-=sum[x];
            m.second=p;
            if(m>mx[0][x])swap(m,mx[0][x]);
            if(m>mx[1][x])swap(m,mx[1][x]);
            comp[x]=0;
        }
        else {
            if(!comp[x])sum[x]+=sum[p];
            else sum[x]=sum[p];
        }
    }
    for(int i=0;i<adj[x].size();++i)
        if(adj[x][i]!=p)dfs1(adj[x][i],x);
}


bool check(int mid){
    for(int i=1;i<=n;++i){
        comp[i]=0;
        sum[i]=0;
        mx[0][i]=mx[1][i]=make_pair(-1,0);
    }
    dfs(1,0,mid);
    //for(int i=1;i<=n;++i)cout<<i<<" "<<sum[i]<<" "<<mx[0][1].first<<" "<<comp[i]<<endl;
    dfs1(1,0);

    for(int i=1;i<=n;++i)if(sum[i]+max(0,mx[0][i].first)>=k)return 1;
    return 0;

}
int main()
{
    cin>>n>>k;
    int u,v;
    for(int i=1;i<=n;++i)scanf("%d",a+i);
    for(int i=1;i<n;++i){
        scanf("%d%d",&u,&v);
        adj[u].push_back(v);
        adj[v].push_back(u);
    }
    int st=0,en=1e6+4,mid;
    //cout<<check(3)<<endl;
    //return 0;
    while(st<en){
        mid=(st+en)/2;
        if(check(mid))st=mid+1;
        else en=mid;
    }
    cout<<st-1<<endl;
}

