int n,k;
vector<int> near[200010];
int sz[200010],a[200010];
bool f=false;

int setsz(int now,int prv){
	int &res=sz[now];
	for(int x:near[now])
		if(x-prv)
			res+=setsz(x,now);
	return ++res;
}

int check(int now,int prv,int x){
	int fix=1;
	int wild1=0,wild2=0;
	priority_queue<int> q;
	for(int y:near[now]){
		if(y-prv){
			int v=check(y,now,x);
			if(v==sz[y])
				fix+=v;
			else{
				if(v>wild1){
					swap(wild1,wild2);
					wild1=v;
				}
				else if(v>wild2)
					wild2=v;
			}
		}
	}
	if(a[now]>=x){
		f|=(fix+wild1+wild2>=k);
		return fix+wild1;
	}
	else
		return 0;
}

int bins(int l,int r){
	int mid=(l+r)/2;
	if(l>r)
		return r;
	f=false;
	check(1,1,mid);
	if(f)
		return bins(mid+1,r);
	else
		return bins(l,mid-1);
}

