const int N = 2e5+5;
int n,sz[N],a[N],c,ret,dp[N];
pair<int,int> m[N][2];
vector<int> adj[N];
void dfs(int x,int p){
	int f=0,u,i,d;dp[x]=0;
	if(!sz[x])f=1,++sz[x];
	d= a[x]>=c ? 1 :0;
	for(i=0;i<adj[x].size();++i){
		u= adj[x][i];
		if(u!=p){
			dfs(u,x);
			if(f)sz[x]+=sz[u];
			if(dp[u]==sz[u])dp[x]+=dp[u];
			else{
				if(m[x][0].first<=dp[u])
					m[x][1]= m[x][0],m[x][0]=make_pair(dp[u],u);
				else if(m[x][1].first<dp[u])
					m[x][1]= make_pair(dp[u],u);
			}
		}
	}
	dp[x]+=d;
	if(d==0)dp[x]=0;
}
int ar[3];
void dfs2(int x,int p,int d){
	int t=d,u,i,v=0,nd,test,z;
	test= dp[x]+ m[x][0].first;
	if(a[x]>=c){
		if(d==n-sz[x])t=0,v=d,test= dp[x]+d;
		else
			test= dp[x]+ max(d,m[x][0].first);
		ar[0]= m[x][0].first,ar[1]= m[x][1].first,ar[2]= t;
		sort(ar,ar+3);
		ret= max(ret,dp[x]+ ar[2]+ar[1]+v);
	}
	for(i=0;i<adj[x].size();++i){
		u= adj[x][i];
		if(u!=p){
			nd=0;z=0;
			if(a[x]>=c){
				nd= dp[x]+v+ max(t,m[x][0].first);
				z= test;
				if(sz[u]==dp[u]){
					nd= dp[x]-sz[u]+ max(m[x][0].first,t)+ v;
					z= test-sz[u];
				}
				else if(u==m[x][0].second){
					nd= dp[x] + v + max(m[x][1].first,t);
					if(d==n-sz[x])z= test-m[x][0].first+m[x][1].first;
					else
						z= dp[x]+max(d,m[x][1].first);
				}
			}
			assert(z==nd);
			dfs2(u,x,nd);
		}
	}
}
