void exgcd(ll a,ll b,ll& d,ll& x,ll& y)
{
    if(!b) { d = a; x = 1; y = 0; }
    else{ exgcd(b, a%b, d, y, x); y -= x*(a/b); }
}

ll inv(ll a, ll p)
{
    ll d, x, y;
    exgcd(a, p, d, x, y);
    return d == 1 ? (x+p)%p : -1;
}

ll dp(ll n, int k)
{
	if(!k) return n;
	if(f[mp(n,k)]) return f[mp(n,k)];
	ll tmp = 0; int overall = 0;
	for(ll i = 1; i * i <= n; ++ i)
		if(n%i == 0) 
		{
			tmp = (tmp + dp(i, k-1)) % P;
			if(i * i == n) overall ++; 
			else {overall += 2; tmp = (tmp + dp(n/i,k-1)) % P;}
		}
	return f[mp(n,k)] = (tmp * inv(overall, P))%P;
}

