ll fs(ll s, ll ss, ll n){
	ll res = 1;
	while (ss > 0){
		if (ss & 1){
			res = (res * s) % n;
		}
		ss >>= 1;
		s = (s * s) % n;
	}
	return res;
}

ll rec(ll ss, ll n){
	if (ss <= 0){
		return fs(var, n, mod);
	}
	ll &re = dp[ss][n];
	if (re != -1){
		return re;
	}
	re = 0;
	ll rep = fs(n + 1, mod - 2, mod);
	for (int i = 0; i <= n; i++){
		re = (re + (rec(ss - 1, n - i) * rep % mod)) % mod;
	}
	return re;
}

