int Nanase(int p)
{
	int low = 0;
	int high = 0;
	int end = 0;

	ql[end] = 0; qr[end] = 2; qans[end++] = 0;
	cnt[0] = (cnt[0] + 2LL*p-3)%MODULE;

	// delta
	for(int i = 3;i < p;)
	{
		while(low < end && qr[low] < i/3) low++;
		while(high < end && qr[high] < i-i/3) high++;
		int sved = 0;
		int q = qr[high];
		for(int j = 0;j < 3;j++)
		{
			if((3*q-j) % 2) continue;
			if((3*q-j) / 2 % 3 != j) continue;
			sved = max(sved,(3*q-j)/2);
		}
		//printf("sved = %d\n",sved);
		int till = min(qr[low]*3+2,sved);
		till = min(till,p-1);

		int mexa[3] = {0};
		mexa[qans[low]]++; mexa[qans[high]]++;
		for(int j = 0;j < 3;j++)
		{
			if(!mexa[j])
			{
				if(end && j == qans[end-1]) qr[end-1] = till;
				else { ql[end] = i; qr[end] = till; qans[end++] = j; }

				int len = till-i+1;
				cnt[j] += ( (ll)p*len%MODULE-(ll)(i+till)*len/2%MODULE+MODULE ) % MODULE;
				cnt[j] %= MODULE;
				break;
			}
		}

		i = till+1;
	}
	return end;
}

