using namespace std;

int sg[maxk];

int wz[maxk];

int n,p;

int f[maxn][4];

int tot;

ll num[4];

int mex(int x,int y){
	if (x*y>0) return 0;
	if (x==1 || y==1) return 2;
	return 1;
}

int main(){
	scanf("%d%d",&n,&p);
	wz[1]=1;
	sg[1]=0;
	tot=1;
	int las1=1,las2=1,noww=3;
	while (noww<p){
		tot++;
		wz[tot]=noww;
		sg[tot]=mex(sg[las1],sg[las2]);
		if (sg[tot]==sg[tot-1]) tot--;
		noww=min(1ll*wz[las2+1] * 3,(1ll*(wz[las1+1]-1)*3/2+1));
		noww=min(noww,p);
		if (sg[las1+1]==noww-noww/3) las1++;
		if (sg[las2+1]==noww / 3) las2++;
	}
	fo(i,1,tot) {
		int l=wz[i];
		int r=wz[i+1];
		if (i==tot) r=p;
		num[sg[i]]=(num[sg[i]]+1ll*p*(r-l)-1ll*r*(r-1)/2+1ll*l*(l-1)/2) % mo;
		num[sg[i]]=(num[sg[i]] % mo+mo) % mo;
	}
	f[0][0]=1;
	fo(i,1,n)
		fo(j,0,3)
			fo(k,0,2) f[i][j ^ k]=(f[i][j ^ k]+f[i-1][j]*1ll*num[k])	% mo;
	ll ans=f[n][1]+f[n][2]+f[n][3];
	printf("%I64d",ans % mo);
	return 0;
}

