#include <bits/stdc++.h>

using namespace std;

typedef pair <int,int> pii;

const int N = 200200;
const int M = 300300;

struct Edge
{
    int x, y;
    Edge(int x = 0, int y = 0) : x(x), y(y) {}
};

struct Query
{
    int T, x;
    Query(int T = 0, int x = 0) : T(T), x(x) {}
};

int a[N];
int n, m, q;
vector <Edge> edges;
vector <Query> queries;

int P[N];
bool InTree[M];
set <pii> Nodes[N];
stack <pii> PChanges;
stack <stack <pii>> NodesInChanges;
stack <stack <pii>> NodesOutChanges;

void Init()
{
    for(int x = 1; x <= n; x++) P[x] = x, Nodes[x].emplace(a[x], x);
}

int F(int x)
{
    while(x != P[x])
        x = P[x];
    return x;
}

void Un(int e, bool storeChanges)
{
    int x = edges[e].x;
    int y = edges[e].y;

    if((x = F(x)) == (y = F(y)))
        return ;

    InTree[e] = true;

    if(Nodes[x].size() < Nodes[y].size())
        swap(x, y);

    if(storeChanges)
    {
        NodesInChanges.push(stack <pii> ());
        NodesOutChanges.push(stack <pii> ());
    }

    for(pii p : Nodes[y])
    {
        if(storeChanges)
        {
            NodesInChanges.top().emplace(x, p.second);
            NodesOutChanges.top().emplace(y, p.second);
        }
    }

    for(pii p : Nodes[y])
        Nodes[x].insert(p);
    Nodes[y].clear();

    if(storeChanges)
        PChanges.emplace(y, P[y]);

    P[y] = x;
}

void DeUn(int e)
{
    if(!InTree[e])
        return ;

    P[PChanges.top().first] = PChanges.top().second;

    while(!NodesInChanges.top().empty())
    {
        pii p = NodesInChanges.top().top();
        NodesInChanges.top().pop();
        Nodes[p.first].erase(pii(a[p.second], p.second));
    }

    while(!NodesOutChanges.top().empty())
    {
        pii p = NodesOutChanges.top().top();
        NodesOutChanges.top().pop();
        Nodes[p.first].insert(pii(a[p.second], p.second));
    }

    PChanges.pop();
    NodesInChanges.pop();
    NodesOutChanges.pop();
}

int main()
{
    cin >> n >> m >> q;

    for(int x = 1; x <= n; x++) scanf("%d", a + x);

    for(int i = 0; i < m; i++)
    {
        int x, y;
        scanf("%d%d", &x, &y);
        edges.emplace_back(x, y);
    }

    for(int i = 0; i < q; i++)
    {
        int T, x;
        scanf("%d%d", &T, &x);
        if(T == 2)  x--;
        queries.emplace_back(T, x);
    }

    vector <bool> LazyUn(m);
    for(const Query& Q : queries)
        if(Q.T == 2)
            LazyUn[Q.x] = true;

    Init();
    for(int i = 0; i < m; i++)
        if(!LazyUn[i])
            Un(i, false);

    reverse(queries.begin(), queries.end());
    for(const Query& Q : queries)
        if(Q.T == 2)
            Un(Q.x, true);

    reverse(queries.begin(), queries.end());
    for(const Query& Q : queries)
    {
        if(Q.T == 1)
        {
            int x = F(Q.x);
            int y = (*Nodes[x].rbegin()).second;
            printf("%d\n", a[y]);

            Nodes[x].erase(pii(a[y], y));
            a[y] = 0;
            Nodes[x].insert(pii(a[y], y));
        }

        else
        {
            DeUn(Q.x);
        }
    }
}

