const int mod=998244353,maxn=131072;
int n,m,i,j,g[50005],g0[50005],g1[50005],g2[50005],f0[50005],f1[50005],f2[50005],w[maxn+5],tmp[maxn+5],ans;
void solve(int l,int r)
{
	if(l==r){
		f0[l]=(f0[l]+g0[l])%mod;
		f1[l]=(f1[l]+g1[l])%mod;
		f2[l]=(f2[l]+g2[l])%mod;
		return;
	}
	int mid=(l+r)/2,i;
	solve(l,mid); 
	poly_mul(f0+l,mid-l+1,g0,r-l+1,tmp+l,r-l+1);
	forup(i,mid+1,r) f0[i]=(f0[i]+tmp[i])%mod;
	poly_mul(f0+l,mid-l+1,g1,r-l+1,tmp+l,r-l+1);
