int gcd(int x, int y) { return y == 0 ? x : gcd(y, x % y); }

void init()
{
    for(int i = 1; i < 512; ++i)
    {
        s[i] = 1;
        for(int j = 2; j < 10; ++j)
        {
            if(i & (1 << (j - 1)))
            {
                s[i] = (s[i] * j) / gcd(s[i], j);
            }
        }
    }
}

lng dfs(int pos, int sta, int pre, int ok, int msta)
{
    if(pos == -1) return sta == msta && pre == 0;
    if((msta | sta) != msta) return 0;
    if(!ok && dp[pos][sta][pre] != -1) return dp[pos][sta][pre];
    lng ans = dfs(pos - 1, sta, (pre * 10) % s[msta], ok && num[pos] == 0, msta);
    int end = ok ? num[pos] : 9;
    for(int i = 1; i <= end; ++i)
    {
        int tpre = ((pre * 10) + i) % s[msta];
        ans += dfs(pos - 1, sta | (1 << (i - 1)), tpre, ok && i == end, msta);
    }
    if(!ok) dp[pos][sta][pre] = ans;
    return ans;
}

int count_bit(int n) { return n == 0 ? 0 : n % 2 + count_bit(n / 2); }

lng calc(lng n)
{
    tot = 0;
    while(n) { num[tot++] = n % 10; n /= 10; }
    lng ans = 0;
    for(int i = 1; i < 512; ++i)
    {
        if(count_bit(i) > tot) continue;
        for(int j = 0; j < tot; ++j)
        {
            for(int k = i; k; k = (k - 1) & i)
                for(int q = 0; q < s[i]; ++q) dp[j][k][q] = -1;
            for(int q = 0; q < s[i]; ++q) dp[j][0][q] = -1;
        }
        ans += dfs(tot - 1, 0, 0, 1, i);
    }
    return ans;
}

