	else w2 = 0;
	if(w1 != w2) return 0;
	for(int i = a, j = b; i > 0 && j > 0; i--, j--)
		if(i == c && j == d) return 1;
	for(int i = a, j = b; i <= 8 && j <= 8; i++, j++)
		if(i == c && j == d) return 1;
	for(int i = a, j = b; i <= 8 && j > 0; i++, j--)
		if(i == c && j == d) return 1;
	for(int i = a, j = b; i > 0 && j <= 8; i--, j++)
		if(i == c && j == d) return 1;
	return 2;
}

int g(int aa, int bb, int v) {
	int ret;
	if(aa == c && bb == d) return v;
	if(aa < c && bb < d) ret = g(aa+1, bb+1, v+1);
	if(aa > c && bb > d) ret = g(aa-1, bb-1, v+1);
	if(aa < c && bb > d) ret = g(aa+1, bb-1, v+1);
	if(aa > c && bb < d) ret = g(aa-1, bb+1, v+1);
	if(aa == c && bb > d) ret = g(a, bb-1, v+1);
	if(aa == c && bb < d) ret = g(a, bb+1, v+1);
	if(aa > c && bb == d) ret = g(a-1, bb, v+1);
	if(aa < c && bb == d) ret = g(a+1, bb, v+1);
	return ret;
}

