  //  print(stat[mn]);
  rep(i, mn-level) {
    ans_f.pb(from);
    ans_t.pb(to-1);
  }
  int pos_f = lower_bound(stat[mn].begin(), stat[mn].end(), from) - stat[mn].begin(); 
  int pos_t = upper_bound(stat[mn].begin(), stat[mn].end(), to) - stat[mn].begin();
  //  cout << "pos result : " << pos_f << " " << pos_t << endl;

  if(from < stat[mn][pos_f]) {
    go(from, stat[mn][pos_f], mn, rmq);
  }
  for(int i = pos_f; i < pos_t-1; ++i) {
    //    cout << "pos : " <<  i << endl;
    go(stat[mn][i]+1, stat[mn][i+1], mn, rmq);
  }
  if(stat[mn][pos_t-1]+1 < to) {
    go(stat[mn][pos_t-1]+1, to, mn, rmq);
  }
}

