// a*b%c
ll mulmod(ll a, ll b, ll c) {
	ll x = 0, y = a%c;
	while (b > 0) {
		if (b & 1)
			x = (x + y) % c;
		y = (y << 1) % c;
		b >>= 1;
	}
	return x%c;
}

//a^b%c
ll expmod(ll a, ll b, ll c) {
	if (b) {
		if (b & 1)
			return mulmod(a, expmod(a, b - 1, c), c);
		else
			return expmod(mulmod(a, a, c), b >> 1, c);
	}
	else {
		return 1;
	}
}

