int Result = 0;

void DFS(int Idx, vector<vector<int> >& Adj, vector<int>& Temp, vector<int>& Set)
{
    int i, j;

    if(Idx == Temp.size())
    {
        j = 0;
        for(i = 0; i < Set.size(); i++)
        {
            if(Set[i])
            {
                j++;
            }
        }

        Result = max(Result, j);
        return;
    }

    for(i = 0; i < 6; i++)
    {
        vector<int> Temp1 = Set;
        Temp[Idx] = i;

        for(j = 0; j < Idx; j++)
        {
            if(Adj[Idx][j])
            {
                Temp1[((min(Temp[Idx], Temp[j]) * Temp.size()) + max(Temp[Idx], Temp[j]))] = 1;
            }
        }

        DFS((Idx + 1), Adj, Temp, Temp1);
    }
}

