#include <bits/stdc++.h>

using namespace std;

const int MAXN = 503;
const long long MOD = 1e9 + 7;

int n, k;
void read()
{
	cin >> n >> k;
}

long long dp[MAXN][MAXN], next_dp[MAXN][MAXN], suffix_dp[MAXN][MAXN];
long long pow2[MAXN];

long long add(long long a, long long b)
{
	a += b;

	if (a >= MOD)
		a -= MOD; 

	return a;
}
i
void solve()
{
	pow2[0] = 1;
	for (int i = 1; i < MAXN; i++)
		pow2[i] = pow2[i-1] * 2 % MOD;

	dp[1][1] = dp[0][2] = 1; 
	for (int i = 0; i <= k; i++)
		for (int j = n + 1; j >= 1; j--)
			suffix_dp[i][j] = suffix_dp[i][j + 1] + dp[i][j];

	for (int i = 2; i <= n; i++)
	{
		memset(next_dp, 0, sizeof(next_dp));

		next_dp[0][i + 1] = 1;
		for (int j = 1; j <= k; j++)
			{
				for (int p = 2; p <= i; p++)
					next_dp[j][p] = dp[j][p-1] * pow2[i - p + 1] % MOD; 

				for (int l = i; l >= 1; l--)
						next_dp[j][1] = add(next_dp[j][1], pow2[l - 1] * suffix_dp[j-1][i - l + 1] % MOD);  
			}

		for (int j = 0; j <= k; j++)
			for (int p = 0; p <= i + 1; p++)
				dp[j][p] = next_dp[j][p];

		memset(suffix_dp, 0, sizeof(suffix_dp));

		for (int i = 0; i <= k; i++)
			for (int j = n + 1; j >= 1; j--)
				suffix_dp[i][j] = add(suffix_dp[i][j + 1], dp[i][j]);
	}

	int ans = 0;

	for (int i = 1; i <= n + 1; i++)
		ans = add(ans, dp[k][i]);

	cout << ans << endl;
}

int main()
{
	ios_base::sync_with_stdio(false);
	cin.tie(nullptr);
	cout.tie(nullptr);

	read();
	solve();
}

