using namespace std;

int hm,am,dm;
int hy,ay,dy;
int ch,ca,cd;

struct Status {
    int h,a,d;
    int c;
    Status(int hh, int aa, int dd, int cc) {
	h = hh, a = aa, d = dd, c = cc;
    }
    bool operator < (const Status& xx) const{
	return c > xx.c;
    }
};

bool judge(Status x) {
    int temp = 0;
    if(x.a <= dm) return false;
    if(hm % (x.a-dm) == 0) temp = hm / (x.a - dm);
    else temp = (int)(hm / (x.a - dm)) + 1;
    return (am-x.d)*temp < x.h;
}

bool vis[505][505][505];

bool check(int x, int y , int z) {
    return x < 505 && y < 505 && z < 505;
}

Status bfs() {
    priority_queue <Status> heap;
    vis[hy][ay][dy] = true;
    heap.push(Status(hy,ay,dy,0));
    while(!heap.empty()) {
	Status cur = heap.top();
	heap.pop();
	if(judge(cur)) {
	    return cur;
	}
	if(check(cur.h+1, cur.a, cur.d)){
