	    }
	} else {
	    heap.push(Status(cur.h+1, cur.a, cur.d, cur.c + ch));
	}

	if(check(cur.h, cur.a+1, cur.d)) {
	    if(!vis[cur.h][cur.a+1][cur.d]) {
		vis[cur.h][cur.a+1][cur.d] = true;
		heap.push(Status(cur.h, cur.a+1, cur.d, cur.c + ca));
	    }
	} else {
	    heap.push(Status(cur.h, cur.a+1, cur.d, cur.c + ca));
	}

	if(check(cur.h, cur.a, cur.d+1)) {
	    if(!vis[cur.h][cur.a][cur.d+1]) {
		vis[cur.h][cur.a][cur.d+1] = true;
		heap.push(Status(cur.h, cur.a, cur.d+1, cur.c + cd));
	    }
	} else {
	    heap.push(Status(cur.h, cur.a, cur.d+1, cur.c + cd));
	}
    }
    return Status(0,0,0,0);
}

