int mul (int a, int b) { return (llint)a * b % MOD; }
int add (int a, int b) { return ((llint)a + b + (llint)2 * MOD) % MOD; }

int n, k, dp[MAXN][MAXN], pw[MAXN*MAXN];

int main ()
{
    scanf ("%d%d", &n, &k);

    pw[0] = 1;
    for (int i = 1; i < MAXN * MAXN; ++i)
        pw[i] = mul(pw[i-1], 2);

    dp[0][0] = 1;

    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < k; ++j) {
            for (int ii = i; ii < n; ++ii) {
                int t = pw[(ii-i+1)*(n-1-ii)];
                t = mul( add(dp[ii-i+1], -1), t );
                dp[i+1][ii+1] = add(dp[i+1][ii+1], mul(t, dp[i][ii]));
            }
        }   
    }

    int ans = 0;
    for (int i = 0; i <= n; ++i)
        ans = add(ans, dp[i][k]);

    printf ("%d\n", ans);

    return 0;
}

