        c[i]=bin(c[i],m-2);
        c[i]=(c[i]*b)%m;
        gg.pb(mp(c[i],i));
    }
    sort(all(gg));

    int anss=0;
    int g1;
    for(int i=0;i<m;++i)
    {
        g1=i;
        if(i==m-1)g1=0;

        l=0;
        r=x/(m)+1;
        while(l<r)
        {
            mid=(l+r+1)/2;
            if(i+mid*(m)<=x)l=mid;
            else r=mid-1;
        }

        if(i+l*(m)>x)continue;

        r=l;
        l=0;
        l+=g1;
        r+=g1;
        if(l%(m-1)<=r%(m-1)&&abs(r-l)<m-1)
        {
            ans+=get_kol(l,r,i);
        }else
        {
            anss+=get_kol(l,m-2,i);
            int p1=r%(m-1);
            ans+=get_kol(0,p1,i);

            r-=p1+1;
            int vgg=r/(m-1);
            ans+=get_kol(0,m-1,i)*vgg;
        }
    }
    cout<<ans-good(0);
}
