#define maxk 2000005
#define mod 1000000007
using namespace std;

int k,w;
long long factorial[maxk],inverse[maxk];

long long power(int a,int x) {
  if (!x) return 1;
  long long q = power(a,x/2);
  q = (q * q) % mod;
  if (x & 1) q = (q * a) % mod;
  return q;
}

long long binom(int a,int b) {
  if (a < b) return 0;
  long long denominator = (inverse[b] * inverse[a - b]) % mod;
  return (factorial[a] * denominator) % mod;
}

long long Anom(int a,int b) {
  return (binom(a,b) * factorial[b]) % mod;
}

long long func(int ws,int start) {
  long long ret = 0;
  // length of string is at least w + 1
  for (int len = ws + start; len < ws + k; len++) {
    int r1 = len - ws + 1,l2 = ws;
    if (l2 <= r1) {
      int comlen = r1 - l2 + 1;
      long long f1 = Anom(k,comlen);
      long long f2 = Anom(k - comlen,len - ws + 1 - comlen);
      f2 = (f2 * f2) % mod;
      ret = (ret + f1 * f2) % mod;
    }
    else {
      long long halves = Anom(k,len - ws + 1);
      halves = (halves * halves) % mod;
      long long free = power(k,len - 2 * (len - ws + 1));
      ret = (ret + halves * free) % mod;
    }
  }
  return ret;
}

int main() {
  factorial[0] = 1;
  for (int i = 1; i < maxk; i++) factorial[i] = (factorial[i - 1] * i) % mod;
  for (int i = 0; i < maxk; i++) inverse[i] = power(factorial[i],mod - 2);
  scanf("%d %d", &k, &w);
  cout << (func(w + 1,0) - func(w,1) + mod) % mod;
}

