long long power(long long a, int b, long long mod){
    long long result = 1;
    while (b) {
        if (b&1) {
            result *= a;
            result %= mod;
        }   
        a *= a;
        a %= mod;
        b >>= 1;
    }
    return result;
}

long long inverse (long long a, long long mod) {
	return power(a,mod-2,mod);
}

long long fact(long long a, long long mod) {
	long long res=1;
	while(a) {
		res *= a;
		res %= mod;
		a--;
	}
	return res;
}

