            tempSum = (tempSum+ (D[h]*(invPerm[h-1]))%MOD)%MOD;

        assert(sum==tempSum);
        D[len] = (D[len] + ((len-k-1)*perm[len-2])%MOD)%MOD + (sum*perm[len-2])%MOD;
        D[len] = D[len]%MOD;
        sum = (sum + (D[len]*invPerm[len-1])%MOD - (D[len-k]*invPerm[len-k-1])%MOD + MOD)%MOD ;
    }
    ll ans = 0;
       rep (i,k,n) {
        int len = i+1;
        ans = (ans + (((D[len]*perm[n-1])%MOD)*(invPerm[len-1]))%MOD)%MOD ;


    }
    cout<<ans;
    return 0;
}

