LLI dfs(int cur, LLI sum) {
    if (sum > s) return 0;
    if (cur == num) {
        if (sum == s) return 1;
        return 0;
    }
    if (memo[cur].count(sum)) return memo[cur][sum];
    LLI res = 0;
    if (a[cur]) res = dfs(cur+1, sum+(1LL<<cur))*2;
    else res = dfs(cur+1, sum) + dfs(cur+1, sum+(1LL<<(cur+1)));
    return memo[cur][sum] = res;
}


