void king(int a, int b, int c, int d)
{
    int count = 0;
    if (a > c && b < d)
    {
        while (a != c || b != d)
        {
            a--;
            b++;
            count++;
        }
    }
    else if (a < c && b < d)
    {
        while (a != c || b != d)
        {
            a++;
            b++;
            count++;
        }
    }
    else if (a < c && b > d)
    {
        while (a != c || b != d)
        {
            a++;
            b--;
            count++;
        }
    }
    else if (a > c && b > d)
    {
        while (a != c || b != d)
        {
            a--;
            b--;
            count++;
        }
    }
    if (a == c && b != d)
    {
        count += abs(b-d);
    }
    else if (b == d && a != c)
    {
        count += abs(a-c);
    }
    cout << count << '\n';
}

