#define ld long double
 #define MOD (ll)(pow(10,9)+7)

ll fact(ll n){
    if(n==1) return 1;
    return (n*fact(n-1))%MOD;
}

ll binsearch(ll n,ll x,ll pos,ll l,ll r){
     ll count=1;
     ll small=x-1;
    ll big=n-x;
    while(l<r){
    ll mid=(l+r)/2;
    if(mid>pos){
        count=(count*big)%MOD;
        big--;
        r=mid;
    }else if(mid<pos){
        count=(count*small)%MOD;
        small--;
        l=mid+1;
    }else break;
    }
    count=(count*fact(big+small))%MOD;
    return count;
}

