int N, T;
int Memo[50][50][50][50][50];

int solve(int pos, int last1, int last2, int count1, int count2) {
    if (pos == N) {
        if (count1 == T && count2 == T-1)
            return 1;
        return 0;
    }
    if (count1 > T)
        return 0;
    if (count2 > T - 1)
        return 0;
    int &ret = Memo[pos][last1][last2][count1][count2];
    if (ret < 0) {
        ret = 0;
        FOR(next, 4) if (next != last2) {
            if (last1 == 4 || last2 == 4) {
                ret += solve(pos + 1, last2, next, count1, count2);
            } else {
                int ncount1 = count1;
                int ncount2 = count2;
                if (last1 < last2 && last2 > next)
                    ++ncount1;
                if (last1 > last2 && last2 < next)
                    ++ncount2;
                ret += solve(pos+1, last2, next, ncount1, ncount2);
            }
        }
    }
    return ret;
}

