    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            priority_queue< pair<int, int> > pq;
            for (int g = 0; g < m; ++g) {               
                pq.push(make_pair<int, int>(-mas[i][g][0] + mas[j][g][1], g));
            }
            int _k = k;
            int _ans = 0;
            while (_k > 0 && !pq.empty() && pq.top().first > 0) {
                int thing = pq.top().second;                
                int amount = min(mas[i][thing][2], _k);
                int delta = pq.top().first * amount;
                _k -= amount;
                _ans += delta;
                pq.pop();
            }
            if (_ans > ans) ans = _ans;
        }
    }
    cout << ans;
}

