#include <bits/stdc++.h>
using namespace std;

typedef long long i64;

vector<i64> getDivs(i64 N) {
    vector<i64> ret;
    for (int i = 1; (i64) i * i <= N; ++i) {
        if (N % i == 0) {
            ret.push_back(i);
            if ((i64) i * i < N) ret.push_back(N / i);
        }
    }
    sort(ret.begin(), ret.end());
    return ret;
}

map<i64, vector<i64>> powers;

bool goodDiv(i64 x) {
    if (x <= 2) return false;
    x--;
    for (int i = 2; (i64) i * i <= x; ++i) {
        if (x % i == 0) {
            while (x % i == 0) x /= i;
            if (x != 1) return false;
            powers[i].push_back(x);
        }
    }
    if (x > 1) powers[x].push_back(x);
    return true;
}

i64 getAns(const vector<i64>& divs) {
    int N = divs.size();
    vector<i64> dp(N, 0);
    dp[0] = 1;
    i64 maxv = divs.back();

    vector<bool> good(N, 0);
    for (int i = 0; i < N; ++i) {
        good[i] = goodDiv(divs[i]);
    }

    for (const vector<i64>& curr: powers) {
        vector<i64> ndp = dp;
        for (i64 p: curr) {
            ++p;
            for (int j = N - 1; j >= 0; --j) {
                if (divs[j] > maxv / p) continue;
                i64 dv = divs[j] * p;
                int pos = lower_bound(divs.begin(), divs.end(), dv) - divs.begin();
                if (pos >= N || divs[pos] != dv) continue;
                dp[pos] += dp[j];
            }
        }
        dp = ndp;
    }
    return dp[N - 1];
}

