}

#include <iostream>
#include <map>
#include <set>
#include <vector>

typedef long long Long;
template <typename K, typename V>
using Map = std::map <K, V>;

const Long N  = 1000000;
const Long N2 = N * N;

#define ALL(v) (v).begin(), (v).end()

std::vector <Long> get_divisors(Long n)
{
    std::vector <Long> divisors;
    for (Long d = 1; d * d <= n; ++ d) {
        if (n % d == 0) {
            divisors.push_back(d);
            if (d * d != n) {
                divisors.push_back(n / d);
            }
        }
    }
    std::sort(ALL(divisors));
    return divisors;
}

std::set <Long> prime_powers;

bool is_prime_power(Long n)
{
    if (n <= 1) {
        return false;
    }
    if (prime_powers.count(n)) {
        return true;
    }
    for (Long d = 2; d * d <= n; ++ d) {
        if (n % d == 0) {
            return false;
        }
    }
    return true;
}

int main()
{
    std::vector <bool> prime(N + 1, true);
    for (int p = 2; p <= N; ++ p) {
        if (prime[p]) {
            for (Long pp = p; pp <= N2; pp *= p) {
                prime_powers.insert(pp);
            }
            if ((long long)p * p <= N) {
                for (int i = p * p; i <= N; i += p) {
                    prime[i] = false;
                }
            }
        }
    }
    Long a;
    std::cin >> a;
    const auto &divisors = get_divisors(a);
    Map <Long, Long> ways;
    ways[a] = 1;
    for (Long d : divisors) {
        if (is_prime_power(d - 1)) {
            Map <Long, Long> new_ways;
            for (const auto &iterator : ways) {
                new_ways[iterator.first] += iterator.second;
                if (iterator.first % d == 0) {
                    new_ways[iterator.first / d] += iterator.second;
                }
            }
            ways.swap(new_ways);
        }
    }
    std::cout << ways[1] << std::endl;
    return 0;
}

