const int MOD = (int)1e9 + 7;
const int MAXN = (int)1e6 + 5;

int rev[MAXN], fact[MAXN];
int dp[MAXN];
int n, k, ans;

int addMod(int a, int b, int m = MOD) {
    a += b;

    if (m <= a) {
        a -= m;
    }

    return a;
}

int mulMod(int a, int b, int m = MOD) {
    return a * 1ll * b % m;
}

int binPow(int a, int b, int m = MOD) {
    int ret = 1;

    while (b > 0) {
        if (b & 1) {
            ret = mulMod(ret, a, m);
        }

        a = mulMod(a, a, m);
        b >>= 1;
    }

    return ret;
}

void pre() {
    fact[0] = rev[0] = 1;

    for (int i = 1; i < MAXN; ++i) {
        fact[i] = mulMod(fact[i - 1], i);
    }

    rev[MAXN - 1] = binPow(fact[MAXN - 1], MOD - 2);

    for (int i = MAXN - 1; i > 0; --i) {
        rev[i - 1] = mulMod(rev[i], i);
    }
}

int C(int n, int k) {
    if (k < 0 || k > n || n < 0) {
        return 0;
    }

    return mulMod(fact[n], mulMod(rev[k], rev[n - k]));
}

int A(int n, int k) {
    if (k < 0 || k > n || n < 0) {
        return 0;
    }

    return mulMod(fact[n], rev[n - k]);
}

void solve() {
    scanf("%d %d", &n, &k);

    for (int i = k + 1; i <= n; ++i) {
        dp[i] = mulMod(i - k - 1, fact[i - 2]);

        for (int j = i - k; j <= i - 1; ++j) {
            dp[i] = addMod(dp[i], mulMod(dp[j], A(i - 2, i - j - 1)));
        }
    }

    for (int i = 1; i <= n; ++i) {
        int cur = mulMod(dp[i], C(n - 1, i - 1));
        cur = mulMod(cur, fact[n - i]);
        ans = addMod(ans, cur);
    }

    printf("%d\n", ans);
}

int main() {
	int tt = 1;
    pre();

	while (tt--) {
		solve();
	}

	return 0;
}

